var siteList = document.querySelector('.site-list');
var userList = document.querySelector('.user-list');
var navToggle = document.querySelector('.main-nav__toggle');

siteList.classList.remove('main-nav--nojs');
userList.classList.remove('main-nav--nojs');

navToggle.addEventListener('click', function() {
  if (siteList.classList.contains('main-nav--closed')) {
    siteList.classList.remove('main-nav--closed');
    siteList.classList.add('main-nav--opened');
  } else {
    siteList.classList.add('main-nav--closed');
    siteList.classList.remove('main-nav--opened');
  }
  if (userList.classList.contains('main-nav--closed')) {
    userList.classList.remove('main-nav--closed');
    userList.classList.add('main-nav--opened');
  } else {
    userList.classList.add('main-nav--closed');
    userList.classList.remove('main-nav--opened');
  }
});
